import React, { Component } from 'react';
import csv from '../services/csv';

const truck = "Caminhão de pequeno porte (1 Ton)"
export default class SearchComponent extends Component {

   state = {
       cities: [],
       truck: "Caminhão de pequeno porte (1 Ton)",
     };

   componentDidMount() {
       const cities = Object.keys(csv);
       this.setState({cities: cities});
       console.log(cities);
   };

   onInputChange = ({ target }) => {
       const value = target.value;
       this.setState({
         [target.name]: value,
       });
   };

   printCitiesList = () => {
       const list = this.state.cities.map((city) => (
         <option
           key={ city }
           value={ city }
         >
           { city }
         </option>
       ));
       return list;
     };

   shipmentTotal = () => {

   };
   render() {
     return (
       <div>
           <p>Consulte o seu transporte: </p>
           <label htmlFor="city-1-input">
           Cidade Origem:
           <select
               data-testid="city-1-input"
               name="city-1"
           >
               { this.printCitiesList() }
           </select>
           </label>
           <label htmlFor="city-2-input">
           Cidade Destino:
           <select
               data-testid="city-2-input"
               name="city-2"
           >
               { this.printCitiesList() }
           </select>
           </label>
           <label htmlFor="truck-input">
               Transporte Selecionado:
               <select
                   data-testid="truck-input"
                   name="truck"
                   value={ this.state.truck }
               >
                   <option value="small-truck">Caminhão de pequeno porte (1 Ton)</option>
                   <option value="medium-truck">Caminhão de médio porte (4 Ton)</option>
                   <option value="large-truck">Caminhão de grande porte (10 Ton)</option>
               </select>
           </label>
           <p></p>
       </div>
     )
   }
}
