import React, { Component } from 'react';
import csv from '../services/csv';

export default class SearchComponent extends Component {
   state = {
       cities: [],
     };
   componentDidMount() {
       const cities = Object.keys(csv);
       this.setState({cities: cities});
   }
   printCitiesList = () => {
       const list = this.state.cities.map((city) => (
         <option
           key={ city }
           value={ city }
         >
           { city }
         </option>
       ));
       return list;
     };
   render() {
     return (
       <div>
           <p>Consulte o seu transporte: </p>
           <label htmlFor="city-1-input">
           Cidade Origem:
           <select
               data-testid="city-1-input"
               name="city-1"
           >
               { this.printCitiesList() }
           </select>
           </label>
           <label htmlFor="city-2-input">
           Cidade Destino:
           <select
               data-testid="city-2-input"
               name="city-2"
           >
               { this.printCitiesList() }
           </select>
           </label>
           <label htmlFor="truck-input">
               Método de pagamento:
               <select
                   data-testid="truck-input"
                   name="truck"
                   value={ truck }
                   onChange={ this.onInputChange }
               >
                   <option value="small-truck">Caminhão de pequeno porte</option>
                   <option value="medium-truck">Caminhão de médio porte</option>
                   <option value="large-truck">Caminhão de grande porte</option>
               </select>
           </label>
       </div>
     )
   }
}
